import { Component } from '@angular/core';

@Component({
  selector: 'app-calculator',
  templateUrl: './calculator.component.html',
  styleUrls: ['./calculator.component.css']
})
export class CalculatorComponent {

  displayValue: string = '0';
  currentInput: string = '';
  operator: string | null = null;
  waitingForSecondOperand: boolean = false;

  onNumberClick(value: string) {
    if (this.waitingForSecondOperand) {
      this.displayValue = value;
      this.waitingForSecondOperand = false;
    } else {
      this.displayValue = this.displayValue === '0' ? value : this.displayValue + value;
    }
  }

  onOperatorClick(op: string) {
    if (this.operator && !this.waitingForSecondOperand) {
      this.calculate();
    }
    this.operator = op;
    this.currentInput = this.displayValue;
    this.waitingForSecondOperand = true;
  }

  calculate() {
    const firstOperand = parseFloat(this.currentInput);
    const secondOperand = parseFloat(this.displayValue);

    if (isNaN(firstOperand) || isNaN(secondOperand) || !this.operator) {
      return;
    }

    switch (this.operator) {
      case '+':
        this.displayValue = (firstOperand + secondOperand).toString();
        break;
      case '-':
        this.displayValue = (firstOperand - secondOperand).toString();
        break;
      case '*':
        this.displayValue = (firstOperand * secondOperand).toString();
        break;
      case '/':
        this.displayValue = (firstOperand / secondOperand).toString();
        break;
      default:
        break;
    }

    this.operator = null;
  }

  onClear() {
    this.displayValue = '0';
    this.currentInput = '';
    this.operator = null;
    this.waitingForSecondOperand = false;
  }

}
